def add_new_media(self, media: Media) -> None:
    """add a new media

    Parameters
    ----------
    media : Media
    """
    if isinstance(media, Book):
        for element in self.media:
            if isinstance(element, Book) and element.isbn == media.isbn:
                self.media.append(media)
    else:
        self.media.append(media)

